		quit:$$$ISERR(tSC)
	}
	quit:$$$ISERR(tSC)


	// index new records and match
	set tSC = $system.iKnow.IndexTable(tDomainName, "Demo_RulesBuilder.Paragraph", "ID", "DocumentName", "FullTextContents", $lb("DocumentName","FullKey"), "DocumentName = '"_pDocumentName_"'")
	quit:$$$ISERR(tSC)

	set tSC = ##class(%iKnow.Matching.MatchingAPI).MatchSources(tDomainId, tDocFilter)
	quit:$$$ISERR(tSC)

